// https://github.com/reactchartjs/react-chartjs-2/blob/master/example/src/charts/Line.js
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Line } from 'react-chartjs-2';

// http://localhost:5000/sleepjournal/id/duration



axios.get('http://localhost:5000/sleepgoals/')
.then (res => {return (res.data)})


console.log('test')
console.log();


// Define dataset to be reflected on line graph
const data = {
  labels: ['Sun', 'Mon', 'Tues', 'Wed', 'Thurs', 'Fri', 'Sat'],
  datasets: [
    {
      label: 'Sleep Duration',
      data: [1, 2, 3, 4], // Edit this with sleep journal duration
      fill: true,
      backgroundColor: 'rgb(3, 0, 145)',
      borderColor: 'rgba(255, 99, 132, 0.2)',
    },
  ],
};

const options = {
  scales: {
    yAxes: [
      {
        ticks: {
          beginAtZero: true,
        },
      },
    ],
  },
};

const LineChart = () => (
  <>
    <div className='header'>
      <h1 className='title'>Sleep Metrics</h1>
      <div className='links'>
        <a
          className='btn btn-gh'
          href='https://github.com/reactchartjs/react-chartjs-2/blob/master/example/src/charts/Line.js'
        >
          Github Source
        </a>
      </div>
    </div>
    <div className="sleepMetrics">
    <Line data={data} options={options} />
    </div>
  </>
);

export default LineChart;
