import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

// 
const Users = props => (
  <tr>
    <td>{props.users.username}</td>
    <td>{props.users.firstName}</td>
    <td>{props.users.lastName}</td>
    <td>{props.users.age}</td>
    <td>
    <a href="#" onClick={() => { props.deleteUser(props.users._id) }}>delete</a>
    </td>
  </tr>
)

// Pass functions to components
export default class UserList extends Component {
  constructor(props) {
    super(props);

    this.deleteUser = this.deleteUser.bind(this)

    this.state = {username: []};
  }

  componentDidMount() {
    axios.get('http://localhost:5000/users/')
      .then(response => {
        this.setState({ username: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  deleteUser(id) {
    axios.delete('http://localhost:5000/users/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
        username: this.state.username.filter(el => el._id !== id)
    })
  }

  userList() {
    return this.state.username.map(currentUser => {
      return <Users usernames={currentUser} deleteUser={this.deleteUser} key={currentUser._id}/>;
    })
  }

  // Render/Return JSX for User List
  render() {
    return (
      <div>
        <h3>Users</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Username</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Age</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            { this.userList() }
          </tbody>
        </table>
      </div>
    )
  }
}
