// https://github.com/reactchartjs/react-chartjs-2/blob/master/example/src/charts/Line.js
import React, { Component, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Line } from 'react-chartjs-2';

// http://localhost:5000/sleepjournal/id/duration


const lineData = {
  labels: ['Sun', 'Mon', 'Tues', 'Wed', 'Thurs', 'Fri', 'Sat'],
  datasets: [
    {
      label: 'Sleep Duration',
      data: {}, // Edit this with sleep journal duration
      fill: true,
      backgroundColor: 'rgb(3, 0, 145)',
      borderColor: 'rgba(255, 99, 132, 0.2)',
    },
  ],
}

export default class SleepMetrics extends Component {
  constructor(props){
    super(props);
    this.onFilterDuration = this.onFilterDuration.bind(this);
    
    this.state = {
      sleepDuration: [],

      }
    };
    

    
componentDidMount() {
  axios.get('http://localhost:5000/sleepjournal/'+this.props.match.params.id)
    .then(response => {
      this.setState({
        username: response.data.username,
        description: response.data.description,
        duration: response.data.duration,
        date: new Date(response.data.date)
      })   
    })
    .catch(function (error) {
      console.log(error);
    })

  axios.get('http://localhost:5000/sleepjournal/')
    .then(response => {
      if (response.data.length > 0) {
        this.setState({
          sleepDuration: response.data.map(lineData => lineData.datasets.data),
        })
      }
    })
    .catch((error) => {
      console.log(error);
    })

}

onFilterDuration(e) {
      this.state({
        duration: e.target.value
      })
    }

    

// Define dataset to be reflected on line graph
  render() {
    return (
      <div>
        <h1>Sleep Metrics</h1>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Sleep Metrics</th>
            </tr>
          </thead>
          <tbody>
            <Line
            data={lineData.datasets.data}
            options={{
              title: {
              display: true,
              text: 'Sleep Metrics'
              },
              legend:{
                display: true,
                position: 'right'
              }
            }}
            />
          </tbody>
        </table>
      </div>
    )
  }
}

//export default SleepMetrics;
