// https://github.com/reactchartjs/react-chartjs-2/blob/master/example/src/charts/Line.js
import React, { Component, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Line } from 'react-chartjs-2';

// http://localhost:5000/sleepjournal/id/duration


export default class SleepMetrics extends Component {
  constructor(props){
    super(props);
    this.state = {
      SleepMetrics: [],
        data: {
          labels: ['Sun', 'Mon', 'Tues', 'Wed', 'Thurs', 'Fri', 'Sat'],
          datasets: [
            {
              label: 'Sleep Duration',
              data: sleepDuration, // Edit this with sleep journal duration
              fill: true,
              backgroundColor: 'rgb(3, 0, 145)',
              borderColor: 'rgba(255, 99, 132, 0.2)',
            },
          ],
        }
      }
    };

  componentDidMount() {
    axios.get('http://localhost:5000/sleepjournal/')
      .then(response => {
        this.setState({ sleepjournal: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  getSleepMetrics() {
    axios
    .get('http://localhost:5000/sleepgoals')
    .then(response => { console.log(response.data)});
    const sleepDuration = response.data; // Sets response data as a variable for mapping
    
    this.setState({ 
      sleepDuration: this.state.data.datasets.forEach(element => {
        SleepMetrics.push(elements.duration);
      })
    }
    )
  }


    sleepDurationMetrics() {
      return this.state.sleepDuration.map(currentsleepduration => {
        return <sleepDurationMetrics key={currentsleepjournal.duration}/>;
      })
    }
    

// Define dataset to be reflected on line graph
  render() {
    return (
      <div>
        <h3>Sleep Goals</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Username</th>
            </tr>
          </thead>
          <tbody>
            { this.sleepDurationMetrics() }
          </tbody>
        </table>
      </div>
    )
  }
}

