        fetch('https://api.datamuse.com/words?rel_rhy=')
        .then(res => res.json())
        .then(
            (result) => {
                this.setState({
                    rhymingWords: result.rhymingWords
                });
            },
            (error) => {
                this.setState({
                    error
                });
            }
        )
